    private void salvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_salvarActionPerformed

        Cadastro cadastro = new Cadastro();
        cadastro.setNome(nome.getText());
        cadastro.setCpf(cpf.getText());
        cadastro.setRg(rg.getText());
        cadastro.setCidade(cidade.getText());
        cadastro.setEndereco(endereco.getText());
        cadastro.setBairro(bairro.getText());
        cadastro.setCep(cep.getText());

        if (masculino.isSelected()) {
            cadastro.setSexo("Masculino");
        } else {
            cadastro.setSexo("Feminino");
        }

        cadastro.save();
//        limpar();
        JOptionPane.showMessageDialog(null, "Cadastro foi Salvo");

    }//GEN-LAST:event_salvarActionPerformed

